import React from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const isLoggedIn = false; // Replace with actual auth check
  const isBlogPage = window.location.pathname === "/blog"; // Check if on BlogPage
  const isCounsellor = true; // Replace with actual user role check

  return (
    <header className="header">
      <div className="logo">Argyll Community Counselling Service</div>
      <nav className="nav">
        <Link to="/">Home</Link>
        <Link to="/blog">Blog</Link>
        {isBlogPage && isCounsellor && (
          <Link to="/add-article" className="add-article-hover-button">
            Add Article
          </Link>
        )}
        <Link to="/team">Team</Link>
        {isLoggedIn ? (
        <Link to="/logout">Log out</Link>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
      </nav>
    </header>
  );
};

export default Header;
