import React from "react";
import "./Home.css";

const content = [
  {
    id: 1,
    title: 'Individual Therapy',
    description: 'One-on-one sessions focused on personal growth.',
    backText: 'Discover how therapy helps build resilience and clarity.',
    image: 'https://via.placeholder.com/150x100',
  },
  {
    id: 2,
    title: 'Family Counselling',
    description: 'Support for families facing challenges together.',
    backText: 'Effective tools for communication and healing.',
    image: 'https://via.placeholder.com/150x100',
  },
  {
    id: 3,
    title: 'Group Sessions',
    description: 'Shared experiences with guided facilitation.',
    backText: 'Build trust, connection, and mutual support.',
  },
  {
    id: 4,
    title: 'Workshops',
    description: 'Focused sessions on specific topics.',
    backText: 'Join our monthly workshops on anxiety, stress & more.',
  },
  {
    id: 5,
    title: 'Youth Counselling',
    description: 'Tailored for teens and young adults.',
    backText: 'Empower youth to face life’s challenges confidently.',
    image: 'https://via.placeholder.com/150x100',
  },
  {
    id: 6,
    title: 'Online Sessions',
    description: 'Flexible and private remote therapy options.',
    backText: 'Book a virtual appointment that fits your schedule.',
  },
];

const Home = () => {
  return (
    <div className="main-container">
      <h1 className="main-title">Welcome to Our Counselling Services</h1>
      <div className="cards-grid">
        {content.map((item) => (
          <div className="flip-card" key={item.id}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <h2>{item.title}</h2>
                <p>{item.description}</p>
                {item.image && (
                  <img src={item.image} alt={item.title} className="card-image" />
                )}
              </div>
              <div className="flip-card-back">
                <p>{item.backText}</p>
                <button className="learn-more-btn">Learn More</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
