import React from 'react';
import Slider from 'react-slick';
import './TeamPage.css';
import img1 from '../../assets/images/team/Lindsey_Gaunt.jpg';

const teamMembers = [
  {
    id: 1,
    name: 'Julie Sinclair',
    bio: 'Specialist in cognitive behavioral therapy with 10+ years of experience.',
    image: '../../assets/images/kitty.jpg',
    link: '/team/sarah-lawson',
  },
  {
    id: 2,
    name: 'Lindsey Gaunt',
    description: 'Lindsey is a qualified pluralistic counsellor, with a Professional Diploma in Psychotherapeutic counselling Practice (Level 5) and a Diploma in Hypnotherapy.',
    bio: 'Lindsey is registered with the National Counselling and Psychotherapy society, and also has a teaching qualification in Mindfulness meditation. Lindsey has been living in Argyll and Bute for 4 years, and also works as a Community Link Worker in various GP practices. Before that, she lived and worked all over the UK and internationally, spending time travelling and working for charities in South America and Africa. Lindsey is a compassionate, person centred counsellor. Lindsey enjoys nature and travel, and loves living in West Scotland, especially the peace and beauty of the surrounding countryside. Lindsey can  provide face to face counselling in Lochgilphead and Ardrishaig, and also works online. She is looking forward to meeting you and working alongside you on your counselling journey.',
    image: src = '../../assets/images/team/Lindsey_Gaunt.jpg',
    link: '/team/james-miller',
  },
  {
    id: 3,
    name: 'Anna Gomez',
    bio: 'Family therapist passionate about relationship dynamics.',
    image: {img1},
    link: '/team/anna-gomez',
  },
];

const TeamPage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    arrows: true,
    responsive: [
      {
        breakpoint: 768, // tablet
        settings: { slidesToShow: 1 },
      },
      {
        breakpoint: 1024, // small laptop
        settings: { slidesToShow: 2 },
      },
    ],
  };

  return (
    <div className="team-container">
      <Slider {...settings}>
  {teamMembers.map((member) => (
    <div key={member.id} className="flip-card">
      <div className="flip-card-inner" onClick={() => window.location.href = member.link}>
        <div className="flip-card-front">
          <img src={member.image} alt={member.name} className="team-image" />
          <h2 className="team-name">{member.name}</h2>
          <p>{member.description}</p>
        </div>
        <div className="flip-card-back">
          <h2 className="team-name">{member.name}</h2>
          <p className="team-bio">{member.bio}</p>
        </div>
      </div>
    </div>
  ))}
</Slider>
    </div>
  );
};

export default TeamPage;
