import React from "react";
import "./Home.css";
import img from '../../components/Header/logo.png';

const content = [
  {
    id: 1,
    title: 'Argyll Community Counselling Service',
    description: 'believes that counselling should be accessible and affordable to everyone who needs it.',
    backText: ['Good quality counselling can make a positive difference to peoples lives in the following way:', 
              'Social connections', 
              'Positive wellbeing', 
              'Stronger families', 
              'Stronger communities', 
              'Ability to access opportunities.'],
    image: {img},
  },
  {
    id: 2,
    title: 'Meet our Team',
    description: 'Support for anyone facing challenges in life.',
    backText: ' ',
  },
  {
    id: 3,
    title: 'Getting help from us',
    description: 'We accept self referrals and referrals from NHS staff and other care agencies. ',
    backText: ['Follow the link to our referral form.', 
      'Complete it and send to:', 
      'admin@argyllcommunitycounselling.org.uk', 
      'Or phone 07872387007', 
      'and Julie will talk you through the referral form over the phone.', 
      'Generally, 8 sessions of counselling will be provided to each person.'],
  },
  {
    id: 4,
    title: 'Some useful article about mental health',
    description: 'Focused sessions on specific topics.',
    backText: ' ',
  },
  {
    id: 5,
    title: 'Other help and support available',
    description: 'Link to NHS, Link to MIND, Link to Samaritans',
    backText: 'Empower youth to face life’s challenges confidently.',
    image: 'https://via.placeholder.com/150x100',
  },
  {
    id: 6,
    title: 'Making a donation',
    description: 'All donations we receive are used to provide more counselling to more people.',
    backText: ['There are a number of ways you can donate:', 
      'Follow the link provided and donate on line', 
      'Through contact with you counsellor', 
      'Via emailing admin@argyllcommunitycounselling.org.uk', 
      'Or phone 07872387007'],
  },
];

const Home = () => {
  return (
    <div className="main-container">
      <div className="cards-grid">
        {content.map((item) => (
          <div className="flip-card" key={item.id}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <h2>{item.title}</h2>
                <p>{item.description}</p>
                {item.image && (
                  <img src={item.image} alt={item.title} className="card-image" />
                )}
              </div>
              <div className="flip-card-back">
                <ul className="card-list">
    {Array.isArray(item.backText) ? (
      item.backText.map((point, idx) => <li key={idx}>{point}</li>)
    ) : (
      <p>{item.backText}</p>
    )}
  </ul>
                <button className="learn-more-btn">Learn More</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
