import React from "react";
import { Link } from "react-router-dom";
import "./Header.css";

const Header = () => {
  const isLoggedIn = false; // Replace with actual auth check
  const isBlogPage = window.location.pathname === "/blog"; // Check if on BlogPage
  const isCounsellor = true; // Replace with actual user role check

  return (
    <header className="header">
      <div className="logo">Argyll Community Counselling Service</div>
      <nav className="nav">
        <div className="nav-item">
        <Link className="nav-link" to="/">Home</Link>
        <Link className="nav-link" to="/blog">Blog</Link>
        {isBlogPage && isCounsellor && (
          <div className="dropdown">
          <Link to="/add-article" className="nav-link add-article-hover-button">
            Add Article
          </Link>
          </div>
        )}
        <Link className="nav-link" to="/team">Team</Link>
        {isLoggedIn ? (
        <Link className="nav-link" to="/logout">Log out</Link>
        ) : (
          <>
            <Link className="nav-link" to="/login">Login</Link>
            <Link className="nav-link" to="/register">Register</Link>
          </>
        )}
        </div>
      </nav>
    </header>
  );
};

export default Header;
