import React from 'react';
import Slider from 'react-slick';
import './TeamPage.css';

const teamMembers = [
  {
    id: 1,
    name: 'Julie Sinclair',
    bio: 'Specialist in cognitive behavioral therapy with 10+ years of experience.',
    image: 'https://via.placeholder.com/300x300',
    link: '/team/sarah-lawson',
  },
  {
    id: 2,
    name: 'Lindsey Gaunt',
    bio: 'Lindsey is a qualified pluralistic counsellor with a Professional Diploma in Psychotherapeutic counselling Practice (Level 5) and a Diploma in Hypnotherapy, and is registered with the National Counselling and Psychotherapy society. Lindsey has been living in Argyll and Bute for 4 years, and also works as a Community Link Worker in various GP practices locally. Before that, she lived and worked all over the UK and internationally, spending time travelling and working in South America and Africa. Lindsey is a compassionate counsellor, whose practice centres on the person-centred approach.  Lindsey enjoys nature and travel, and loves living in West Scotland, where she can raise her family in the peace and beauty of the surrounding countryside. Lindsey is looking forward to meeting you and working alongside you on your counselling journey.',
    image: 'https://via.placeholder.com/300x300',
    link: '/team/james-miller',
  },
  {
    id: 3,
    name: 'Anna Gomez',
    bio: 'Family therapist passionate about relationship dynamics.',
    image: 'https://via.placeholder.com/300x300',
    link: '/team/anna-gomez',
  },
  // Add more members...
];

const TeamPage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    arrows: true,
    responsive: [
      {
        breakpoint: 768, // tablet
        settings: { slidesToShow: 1 },
      },
      {
        breakpoint: 1024, // small laptop
        settings: { slidesToShow: 2 },
      },
    ],
  };

  return (
    <div className="team-container">
      <Slider {...settings}>
        {teamMembers.map((member) => (
          <div
            key={member.id}
            className="team-card"
            onClick={() => window.location.href = member.link}
          >
            <img src={member.image} alt={member.name} className="team-image" />
            <h2 className="team-name">{member.name}</h2>
            <span className="team-bio">{member.bio}</span>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default TeamPage;
