import React from "react";
import "./Home.css";
import img from '../../components/Header/logo.png';

const content = [
  {
    id: 1,
    title: 'Argyll Community Counselling Service',
    description: 'believes that counselling should be accessible and affordable to everyone who needs it.',
    backText: 'We believe that good quality counselling can make a positive difference to peoples lives in the following way: Social connections, Positive wellbeing, Stronger families, Stronger communities, Ability to access opportunities.',
    image: {img},
  },
  {
    id: 2,
    title: 'Meet our Team',
    description: 'Support for anyone facing challenges in life.',
    backText: ' ',
  },
  {
    id: 3,
    title: 'Getting help from us',
    description: 'We accept self referrals and referrals from NHS staff and other care agencies. ',
    backText: 'Follow the link to our referral form. Complete it and send to: admin@argyllcommunitycounselling.org.uk Or phone 07872387007 and Julie will talk you through the referral form over the phone. We will then match you with a counsellor who will contact you to find out more about you and what you hope to get out of counselling. Generally, 8 sessions of counselling will be provided to each person.',
  },
  {
    id: 4,
    title: 'Some useful article about mental health',
    description: 'Focused sessions on specific topics.',
    backText: ' ',
  },
  {
    id: 5,
    title: 'Youth Counselling',
    description: 'Tailored for teens and young adults.',
    backText: 'Empower youth to face life’s challenges confidently.',
    image: 'https://via.placeholder.com/150x100',
  },
  {
    id: 6,
    title: 'Online Sessions',
    description: 'Flexible and private remote therapy options.',
    backText: 'Book a virtual appointment that fits your schedule.',
  },
];

const Home = () => {
  return (
    <div className="main-container">
      <div className="cards-grid">
        {content.map((item) => (
          <div className="flip-card" key={item.id}>
            <div className="flip-card-inner">
              <div className="flip-card-front">
                <h2>{item.title}</h2>
                <p>{item.description}</p>
                {item.image && (
                  <img src={item.image} alt={item.title} className="card-image" />
                )}
              </div>
              <div className="flip-card-back">
                <p>{item.backText}</p>
                <button className="learn-more-btn">Learn More</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
