import React from 'react';
import Slider from 'react-slick';
import './TeamPage.css';

const teamMembers = [
  {
    id: 1,
    name: 'Dr. Sarah Lawson',
    bio: 'Specialist in cognitive behavioral therapy with 10+ years of experience.',
    image: 'https://via.placeholder.com/300x300',
    link: '/team/sarah-lawson',
  },
  {
    id: 2,
    name: 'James Miller',
    bio: 'Certified counsellor focusing on adolescent mental health.',
    image: 'https://via.placeholder.com/300x300',
    link: '/team/james-miller',
  },
  {
    id: 3,
    name: 'Anna Gomez',
    bio: 'Family therapist passionate about relationship dynamics.',
    image: 'https://via.placeholder.com/300x300',
    link: '/team/anna-gomez',
  },
  // Add more members...
];

const TeamPage = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    arrows: true,
    responsive: [
      {
        breakpoint: 768, // tablet
        settings: { slidesToShow: 1 },
      },
      {
        breakpoint: 1024, // small laptop
        settings: { slidesToShow: 2 },
      },
    ],
  };

  return (
    <div className="team-container">
      <h1 className="team-title">Meet Our Team</h1>
      <Slider {...settings}>
        {teamMembers.map((member) => (
          <div
            key={member.id}
            className="team-card"
            onClick={() => window.location.href = member.link}
          >
            <img src={member.image} alt={member.name} className="team-image" />
            <h2 className="team-name">{member.name}</h2>
            <p className="team-bio">{member.bio}</p>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default TeamPage;
